/* eslint-disable jsx-a11y/img-redundant-alt */
// HomePage.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { SlArrowLeftCircle, SlArrowRightCircle } from "react-icons/sl";

import Navbar from "../component/navbar";
import Footer from "../component/footer";
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

import '../style/style.css';
import '../style/bootstrap.min.css';
import '../style/owl.theme.default.min.css';
import '../style/jquery.fancybox.min.css';
import '../style/bootstrap-datepicker.css';

const Signe = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [user, setUser] = useState([]);
  const navigate = useNavigate();


  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    setErrorMessage("");
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setErrorMessage("");
  };

  const handleSignIn = () => {
    fetch("http://localhost:3001/user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        setUser(data.results);
        if (data.message === false) {
          setErrorMessage("Il n'y a pas de compte avec ces informations.");
        } else {
          sessionStorage.setItem("user", JSON.stringify(data.results[0]));
          navigate("/profil");
        }
      })
      .catch((error) => {
        console.error("Erreur lors de la requête :", error);
      });
  };

  return (
    <form method="post" className="form-box">
      <h3 className="h4 text-black mb-4">Sign Up</h3>
      <div className="form-group">
        <input
          type="text"
          name="email"
          autoComplete="email"
          required
          value={email}
          onChange={handleEmailChange}
          className="form-control"
          placeholder="Email Address"
        />
      </div>
      <div className="form-group">
        <input
          type="password"
          className="form-control"
          name="password"
          autoComplete="current-password"
          required
          value={password}
          onChange={handlePasswordChange}
          placeholder="Password"
        />
      </div>
      <div className="form-group">
        <input
          type="button"
          onClick={handleSignIn}
          className="btn btn-primary btn-pill"
          value="Sign up"
        />
      </div>
      { !user &&  (
        <span>{errorMessage}</span>
      )}
    </form>
  );
};


const Contact =()=>{
  const [nom, setNom] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const Envoyer = () => {
    fetch("http://localhost:3001/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({  nom , email , message }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data)
      })
      .catch((error) => {
        console.error("Erreur lors de la requête :", error);
      });
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleNomChange = (e) => {
    setNom(e.target.value);
  };
  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };
  const Effacer = () =>{
    setEmail("");
    setNom("");
    setMessage("");
  }

  return(
    <div className="self-stretch overflow-hidden flex flex-row items-center justify-center py-[60px] px-[170px] relative gap-[60px] z-[8] text-sm">
        <div className="flex-1 flex flex-col items-center justify-start gap-[24px] z-[0]">
          <b className="relative text-21xl leading-[48px] inline-block text-center w-[520px]">
            Contactez-nous
          </b>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Nom
            </div>
            <input
              type="text"
              name="nom"
              onChange={handleNomChange}
              value={nom}
              placeholder="votre nom"
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
            />
          </div>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Email
            </div>
            <input
              type="email"
              name="email"
              onChange={handleEmailChange}
              value={email}
              placeholder="votre email."
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
            />
          </div>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Message
            </div>
            <textarea
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
              name="message"
              rows="4"
              cols="50"
              value={message}
              onChange={handleMessageChange}
              placeholder="votre message."
              />
          </div>
          <div className="overflow-hidden flex flex-row items-start justify-start gap-[12px] text-base">
            <input
              type="button"
              value="Effacer"
              onClick={Effacer}
              className="rounded-lg box-border w-40 flex flex-col items-center justify-center p-3 border-[1px] border-solid border-black"
            />
            <input
              type="button"
              value="Envoyer"
              onClick={Envoyer}
              className="rounded-lg box-border w-40 flex flex-col items-center justify-center p-3 border-[1px] border-solid border-black"
            />
          </div>
        </div>
      </div>
  )
}

const PopuVideos =()=>{
  const [videos, setVideos] = useState([]);
  const [Tutorials,setTutorials] = useState([]);
  const [videosTrie, setVideosTrie] = useState([]);

  useEffect(() => {
    const fetchvideos = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/video`
        );
        const data = await response.json();
        setVideos(data.videos);
        //console.log(data);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };
    const fetchTutorials = async () => {
      try {
        const response = await fetch("http://localhost:3001/Tutorial");
        const data = await response.json();
        setTutorials(data.tutorialsWithVideos);
        //console.log(data);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };
    const sortByNbVueDesc = () => {
      const sortedVideos = [...videos].sort((a, b) => b.nbvue - a.nbvue);
      setVideosTrie(sortedVideos);
    };

    fetchTutorials();
    fetchvideos();
    sortByNbVueDesc();
  }, []);


  

  const getTutorialNameById = (tutorialId) => {
    const tutorial = Tutorials.find((t) => t.id === tutorialId);
    return tutorial ? tutorial.NomTutorial : "";
  };

  return(
    <section>
    <div className="popular page_section">
		<div className="container">
			<div className="row">
				<div className="col">
					<div className="section_title text-center">
						<h1>Popular Videos</h1>
					</div>
				</div>
			</div>

			<div className="row course_boxes">
				{videosTrie.slice(0,3).map((video) => (
				<div className="col-lg-4 course_box" key={video.id} >
					<div className="card">
						<img className="card-img-top" src="images/course_1.jpg" alt="image"/>
						<div className="card-body text-center">
							<div className="card-title"><a href={`/categories/Tutorial/${getTutorialNameById(video.tutorial)}/${video.nomvideo}`}>{video.nomvideo}</a></div>
							<div className="card-text">{video.descreption}</div>
						</div>
					</div>
				</div>
        ))}
			</div>
		</div>		
	</div>
  </section>
  )
}




const CustomPrevArrow = (props) => (
  <div {...props} className="slick-prev">
    <SlArrowLeftCircle />
  </div>
);

const CustomNextArrow = (props) => (
  <div {...props} className="slick-next">
    <SlArrowRightCircle/>
  </div>
);








function HomePage() {
  const userString = sessionStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;
  const [Categories, setCategories] = useState([]);
  const [Tutorials, setTutorials] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(0);
  const [filteredTutorials, setFilteredTutorials] = useState(Tutorials);


  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch("http://localhost:3001/categories");
        const data = await response.json();
        setCategories(data.categories);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    const fetchTutorialsWithVideos = async () => {
      try {
        const response = await fetch("http://localhost:3001/Tutorial");
        const data = await response.json();
        setTutorials(data.tutorialsWithVideos);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    fetchTutorialsWithVideos();
    fetchCategories();

  }, []); 

  useEffect(() => {
    if (selectedCategory === 0) {
      setFilteredTutorials(Tutorials);
    } else {
      const filtered = Tutorials.filter(tutorial => tutorial.categorie === selectedCategory);
      setFilteredTutorials(filtered);
    }
  }, [selectedCategory, Tutorials]);


  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
          margin: 20,
          prevArrow: <CustomPrevArrow />,
          nextArrow: <CustomNextArrow />, 
        }
      },
      {
        breakpoint: 1000,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 5,
          infinite: true,
          dots: true,
          margin: 30,
          prevArrow: <CustomPrevArrow />, 
          nextArrow: <CustomNextArrow />,
        }
      }
    ]
  };

  return (
    <>
      <Navbar />
      <section className="intro-section" id="home-section">
        {!userString ? (
        <div className="slide-1" data-stellar-background-ratio="0.5">
          <div className="container">
            <div className="row align-items-center">
              <div className="col-12">
                <div className="row align-items-center">
                  <div className="col-lg-6 mb-4">
                    <h1 >
                      Learn From The Expert
                    </h1>
                    <p className="mb-4" >
                      Lorem ipsum dolor sit amet consectetur adipisicing elit.
                      Maxime ipsa nulla sed quis rerum amet natus quas
                      necessitatibus.
                    </p>
                    <p data-aos="fade-up" data-aos-delay="300">
                      <a
                        href="/"
                        className="btn btn-primary py-3 px-5 btn-pill"
                      >
                        Admission Now
                      </a>
                    </p>
                  </div>
                  
                  <div
                    className="col-lg-5 ml-auto"
                    data-aos="fade-up"
                    data-aos-delay="500"
                  >
                    <Signe />
                  </div>
                  
                </div>
              </div>
            </div>
          </div>
        </div>
      ):(
          <div className="slide-1" data-stellar-background-ratio="0.5">
          <div className="container">
  <div className="row justify-content-center align-items-center">
    <div className="col-lg-6 mb-4 text-center">
      <h1>
        Learn From The Expert
      </h1>
      <p className="mb-4">
        Lorem ipsum dolor sit amet consectetur adipisicing elit.
        Maxime ipsa nulla sed quis rerum amet natus quas
        necessitatibus.
      </p>
      <p data-aos="fade-up" data-aos-delay="300">
        <a
          href="/"
          className="btn btn-primary py-3 px-5 btn-pill"
        >
          Admission Now
        </a>
      </p>
    </div>
  </div>
</div>
        </div>
      )}
    </section>
    <section id="Categories-section">
      <div>
        <div className="site-section courses-title" id="courses-section">
          <div className="container">
            <div className="row mb-5 justify-content-center">
              <div className="col-lg-7 text-center">
                <h2 className="section-title">Categories</h2>
              </div>
            </div>
          </div>
        </div>

        <div className="site-section courses-entry-wrap">
          <div className="container">
              <div style={{ padding: '0 30px' }}>
                <Slider {...settings} currentSlide={1} slideCount={3}>
                  {Categories.map((category) => (
                    <div className="List w-96 h-96 py-5 justify-center items-center gap-10 inline-flex" style={{ paddingRight: '15px' }} key={category.id}>
                  <div className="course bg-white h-100 valign-self-stretch">
              <figure className="m-0">
                <img src={category.imagePath} alt={category.imagePath} className="img-fluid"/>
              </figure>
              <div className="course-inner-text py-4 px-4">
                <div className="meta"><span className="icon-clock-o"></span></div>
                <h3><a href={`categories/${category.Nomcategorie}`}>{category.Nomcategorie }</a></h3>
                <p>{category.descreption} </p>
              </div>
            </div>
                </div>
                ))}
              </Slider>
              </div>
            </div>
          </div>
        </div>
      </section>
<section id="Tutorial-section">
      <div className="container">
			<div className="section-title mb-0">
				<h2>Tutorial</h2>
				<p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec malesuada lorem maximus mauris scelerisque, at rutrum nulla dictum. Ut ac ligula sapien. Suspendisse cursus faucibus finibus.</p>
			</div>
		</div>
		<div className="course-warp">
    <ul className="course-filter controls">
  <li
    key="all"
    className={`control ${selectedCategory === 0 ? 'active' : ''}`}
    onClick={() => setSelectedCategory(0)}
  >
    All
  </li>
  {Categories.map((category) => (
    <li
      key={category.id}
      className={`control ${selectedCategory === category.id ? 'active' : ''}`}
      onClick={() => setSelectedCategory(category.id)}
    >
      {category.Nomcategorie}
    </li>
  ))}
</ul>                                     
			<div className="row course-items-area">
      {filteredTutorials.map((tutorial) => (
				<div className="mix col-lg-3 col-md-4 col-sm-6 finance" key={tutorial.id}>
					<div className="course-item">
          <a
                href={`categories/Tutorial/${tutorial.NomTutorial}`}
                className="flex-1 rounded-md box-border h-[472px] overflow-hidden flex flex-col items-center justify-start border-[1px] border-solid border-gray-500"
              >
                <div className="self-stretch h-[340px] overflow-hidden shrink-0 flex flex-row items-start justify-start">
                  <div className="self-stretch flex-1 relative bg-gainsboro">
                    <div className="absolute w-[calc(100%_-_32px)] top-[calc(50%_-_8px)] left-[16px] leading-[16px] flex items-center justify-center h-4">
                    <img src={tutorial.imagePath} alt="Image" className="img-fluid"/>
                    </div>
                  </div>
                </div>
                <div className="self-stretch flex flex-col items-start justify-start p-3 gap-[4px] text-left text-base">
                  <div className="self-stretch relative leading-[24px]">
                    {tutorial.NomTutorial}
                  </div>

                  <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-center">
                    <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                      😃
                    </div>
                    <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                      ✅
                    </div>
                    <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                      ❤️
                    </div>
                  </div>
                </div>
              </a>
					</div>
				</div>
        ))}
			</div>
		</div>
	</section>
  <p>
    <PopuVideos />
  </p>
  <section id="contact-section">
      {(user===null||user.type==="user")&&(
        <Contact />
      )}
  </section>
  <p></p>
  <Footer />
</>
);
};

export default HomePage;
