import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../component/navbar";
import Footer from "../component/footer";
import { Link, useNavigate } from "react-router-dom";
import VideoItem from "../component/VideoItem";

function VideosPage() {
  const userString = sessionStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;
  const { NomTutorial, Nomvideo } = useParams();
  const [video, setVideo] = useState([]);
  const [videos, setVideos] = useState([]);
  const [Tutorials,setTutorials] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      sessionStorage.setItem("error", JSON.stringify("verifie son compte"));
      navigate("/");
    }
  }, [user, navigate]);

  const EnregestreVue = async () => {
    const id = video.id;
  
    try {
      const response = await fetch(`http://localhost:3001/video/${id}`, {
        method: "PATCH", 
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error("Erreur lors de la requête :", error);
    }
  };
  const Enrehistory = async () => {
    const vue = {
      id_user: user.id,
      id_video: video.id,
    };

    try {
      const response = await fetch("http://localhost:3001/historique", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ vue }),
      });

      const data = await response.json();
      //console.log(data);
    } catch (error) {
      console.error("Erreur lors de la requête :", error);
    }
  };  
  useEffect(() => {
    EnregestreVue();
    Enrehistory(); 
  }, []);
  useEffect(() => {
    const fetchvideos = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/video`
        );
        const data = await response.json();
        setVideos(data.videos);
        //console.log(data);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };
    const fetchTutorials = async () => {
      try {
        const response = await fetch("http://localhost:3001/Tutorial");
        const data = await response.json();
        setTutorials(data.tutorialsWithVideos);
        //console.log(data);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    const fetchVideo = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/Tutorial/${NomTutorial}/${Nomvideo}`
        );
        const data = await response.json();
        //console.log(data);
        setVideo(data.video);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };
    fetchTutorials();
    fetchvideos();
    fetchVideo();
  }, [NomTutorial, Nomvideo]);
  const getTutorialNameById = (tutorialId) => {
    const tutorial = Tutorials.find((t) => t.id === tutorialId);
    return tutorial ? tutorial.NomTutorial : "";
  };

  return (
    <> 
      <div className="Navbar">
         <Navbar/>
      </div>
      <div className="video-page">
        <div className="video-container">
        <video controls width="800" height="500">
            <source src={video.videosPath} type="video/mp4" />
        </video>
        <div>
          <h2>{video.nomvideo}</h2>
          <p>{video.description}</p>
        </div>
        </div>
        <div className="w-[630px] h-[3700px] py-5 flex-col justify-center items-center gap-10 inline-flex flex-grow">
        {videos.map((itemV) => (
          <Link key={itemV.id} to={`/categories/Tutorial/${getTutorialNameById(itemV.tutorial)}/${itemV.nomvideo}`}>
            <VideoItem  video={itemV} />
          </Link>
        ))}
        </div>
        
      </div>
      <Footer/>
    </>
  );
}

export default VideosPage;
