/* eslint-disable jsx-a11y/img-redundant-alt */
// HomePage.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { SlArrowLeftCircle, SlArrowRightCircle } from "react-icons/sl";

import Navbar from "../component/navbar";
import Footer from "../component/footer";
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

import '../style/style.css';
import '../style/bootstrap.min.css';
import '../style/owl.theme.default.min.css';
import '../style/jquery.fancybox.min.css';
import '../style/bootstrap-datepicker.css';

const Signe = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [user, setUser] = useState([]);
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    setErrorMessage("");
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setErrorMessage("");
  };

  const handleSignIn = () => {
    fetch("http://localhost:3001/user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        setUser(data.results);
        if (data.message === false) {
          setErrorMessage("Il n'y a pas de compte avec ces informations.");
        } else {
          sessionStorage.setItem("user", JSON.stringify(data.results[0]));
          navigate("/profil");
        }
      })
      .catch((error) => {
        console.error("Erreur lors de la requête :", error);
      });
  };

  return (
    <form action="" method="post" className="form-box">
      <h3 className="h4 text-black mb-4">Sign Up</h3>
      <div className="form-group">
        <input
          type="text"
          name="email"
          autoComplete="email"
          required
          value={email}
          onChange={handleEmailChange}
          className="form-control"
          placeholder="Email Address"
        />
      </div>
      <div className="form-group">
        <input
          type="password"
          className="form-control"
          name="password"
          autoComplete="current-password"
          required
          value={password}
          onChange={handlePasswordChange}
          placeholder="Password"
        />
      </div>
      <div className="form-group">
        <input
          type="button"
          onClick={handleSignIn}
          className="btn btn-primary btn-pill"
          value="Sign up"
        />
      </div>
      {user.length > 0 && user[0].message === false && (
        <span>{errorMessage}</span>
      )}
    </form>
  );
};
const CustomPrevArrow = (props) => (
  <div {...props} className="slick-prev">
    <SlArrowLeftCircle />
  </div>
);

const CustomNextArrow = (props) => (
  <div {...props} className="slick-next">
    <SlArrowRightCircle/>
  </div>
);

function HomePage() {
  const userString = sessionStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;
  const [Categories, setCategories] = useState([]);
  const [Tutorials, setTutorials] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(0);
  const [filteredTutorials, setFilteredTutorials] = useState(Tutorials);
  const [categoriesWithImages, setCategoriesWithImages] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch("http://localhost:3001/categories");
        const data = await response.json();
        setCategories(data.categories);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    const fetchTutorialsWithVideos = async () => {
      try {
        const response = await fetch("http://localhost:3001/Tutorial");
        const data = await response.json();
        setTutorials(data.tutorialsWithVideos);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    fetchTutorialsWithVideos();
    fetchCategories();

  }, []); // Removed Categories and Tutorials from the dependency array

  useEffect(() => {
    if (selectedCategory === 0) {
      setFilteredTutorials(Tutorials);
    } else {
      const filtered = Tutorials.filter(tutorial => tutorial.tutorial.categorie === selectedCategory);
      setFilteredTutorials(filtered);
    }
  }, [selectedCategory, Tutorials]);

  useEffect(() => {
    const DataImage = async () => {
      const categoriesWithImages = await Promise.all(
        Categories.map(async (category) => {
          if (!category.image.data) return category;
          const imageUrl = URL.createObjectURL(new Blob([category.image.data]));
          return { ...category, image: imageUrl };
        })
      );
      setCategoriesWithImages(categoriesWithImages);
      console.log(categoriesWithImages);
    };

    DataImage();
  }, [Categories]);
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
          margin: 20,
          prevArrow: <CustomPrevArrow />,
          nextArrow: <CustomNextArrow />, 
        }
      },
      {
        breakpoint: 1000,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 5,
          infinite: true,
          dots: true,
          margin: 30,
          prevArrow: <CustomPrevArrow />, 
          nextArrow: <CustomNextArrow />,
        }
      }
    ]
  };

  return (
    <>
      <Navbar />
      <section className="intro-section" id="home-section">
        <div className="slide-1" data-stellar-background-ratio="0.5">
          <div className="container">
            <div className="row align-items-center">
              <div className="col-12">
                <div className="row align-items-center">
                  <div className="col-lg-6 mb-4">
                    <h1 >
                      Learn From The Expert
                    </h1>
                    <p className="mb-4" >
                      Lorem ipsum dolor sit amet consectetur adipisicing elit.
                      Maxime ipsa nulla sed quis rerum amet natus quas
                      necessitatibus.
                    </p>
                    <p data-aos="fade-up" data-aos-delay="300">
                      <a
                        href="/"
                        className="btn btn-primary py-3 px-5 btn-pill"
                      >
                        Admission Now
                      </a>
                    </p>
                  </div>
                  {!userString && (
                  <div
                    className="col-lg-5 ml-auto"
                    data-aos="fade-up"
                    data-aos-delay="500"
                  >
                    <Signe />
                  </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    <section id="Categories-section">
      <div>
        <div className="site-section courses-title" id="courses-section">
          <div className="container">
            <div className="row mb-5 justify-content-center">
              <div className="col-lg-7 text-center">
                <h2 className="section-title">Categories</h2>
              </div>
            </div>
          </div>
        </div>

        <div className="site-section courses-entry-wrap">
          <div className="container">
              <div style={{ padding: '0 30px' }}>
                <Slider {...settings}>
                  {categoriesWithImages.map((category) => (
                    <div className="List w-96 h-96 py-5 justify-center items-center gap-10 inline-flex" style={{ paddingRight: '15px' }} key={category.id}>
                  <div className="course bg-white h-100 valign-self-stretch">
              <figure className="m-0">
                <img src={category.image} alt="Image" className="img-fluid"/>
              </figure>
              <div className="course-inner-text py-4 px-4">
                <div className="meta"><span className="icon-clock-o"></span></div>
                <h3><a href={`categories/${category.Nomcategorie}`}>{category.Nomcategorie }</a></h3>
                <p>{category.descreption} </p>
