/* eslint-disable jsx-a11y/img-redundant-alt */
// HomePage.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useSpring, animated } from "react-spring";

import Navbar from "../component/navbar";
import Footer from "../component/footer";
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

import '../style/style.css';
import '../style/bootstrap.min.css';
import '../style/owl.theme.default.min.css';
import '../style/jquery.fancybox.min.css';
import '../style/bootstrap-datepicker.css';

const Signe = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [user, setUser] = useState([]);
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    setErrorMessage("");
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setErrorMessage("");
  };

  const handleSignIn = () => {
    fetch("http://localhost:3001/user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        setUser(data.results);
        if (data.message === false) {
          setErrorMessage("Il n'y a pas de compte avec ces informations.");
        } else {
          sessionStorage.setItem("user", JSON.stringify(data.results[0]));
          navigate("/profil");
        }
      })
      .catch((error) => {
        console.error("Erreur lors de la requête :", error);
      });
  };

  return (
    <form action="" method="post" className="form-box">
      <h3 className="h4 text-black mb-4">Sign Up</h3>
      <div className="form-group">
        <input
          type="text"
          name="email"
          autoComplete="email"
          required
          value={email}
          onChange={handleEmailChange}
          className="form-control"
          placeholder="Email Address"
        />
      </div>
      <div className="form-group">
        <input
          type="password"
          className="form-control"
          name="password"
          autoComplete="current-password"
          required
          value={password}
          onChange={handlePasswordChange}
          placeholder="Password"
        />
      </div>
      <div className="form-group">
        <input
          type="button"
          onClick={handleSignIn}
          className="btn btn-primary btn-pill"
          value="Sign up"
        />
      </div>
      {user.length > 0 && user[0].message === false && (
        <span>{errorMessage}</span>
      )}
    </form>
  );
};
const CustomPrevArrow = (props) => (
  <div {...props} className="slick-prev">
    {/* Ajoutez votre icône ou texte pour la flèche précédente ici */}
  </div>
);

// Fonction ou composant personnalisé pour la flèche suivante
const CustomNextArrow = (props) => (
  <div {...props} className="slick-next">
    {/* Ajoutez votre icône ou texte pour la flèche suivante ici */}
  </div>
);

function HomePage() {
  const userString = sessionStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;
  const [Categories, setCategories] = useState([]);
  const [Tutorials, setTutorials] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(0);
  const [filteredTutorials, setFilteredTutorials] = useState(Tutorials);
  const [categoriesWithImages, setCategoriesWithImages] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch("http://localhost:3001/categories");
        const data = await response.json();
        setCategories(data.categories);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    const fetchTutorialsWithVideos = async () => {
      try {
        const response = await fetch("http://localhost:3001/Tutorial");
        const data = await response.json();
        setTutorials(data.tutorialsWithVideos);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    fetchTutorialsWithVideos();
    fetchCategories();

  }, []); // Removed Categories and Tutorials from the dependency array

  useEffect(() => {
    if (selectedCategory === 0) {
      setFilteredTutorials(Tutorials);
    } else {
      const filtered = Tutorials.filter(tutorial => tutorial.tutorial.categorie === selectedCategory);
      setFilteredTutorials(filtered);
    }
  }, [selectedCategory, Tutorials]);

  useEffect(() => {
    const DataImage = async () => {
      const categoriesWithImages = await Promise.all(
        Categories.map(async (category) => {
          if (!category.image.data) return category;
          const imageUrl = URL.createObjectURL(new Blob([category.image.data]));
          return { ...category, image: imageUrl };
        })
      );
      setCategoriesWithImages(categoriesWithImages);
      console.log(categoriesWithImages);
    };

    DataImage();
  }, [Categories]);
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
          margin: 20,
          prevArrow: <CustomPrevArrow />,
          nextArrow: <CustomNextArrow />, 
        }
      },
      {
        breakpoint: 1000,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 5,
          infinite: true,
          dots: true,
          margin: 30,
          prevArrow: <CustomPrevArrow />, 
          nextArrow: <CustomNextArrow />,
        }
      }
    ]
  };
  useEffect(() => {
    // Nettoyez l'URL lorsqu'un composant est démonté
    return () => {
      categoriesWithImages.forEach(category => {
        if (category.image instanceof Blob) {
          URL.revokeObjectURL(category.image);
        }
      });
    };
  }, [categoriesWithImages]);
  return (
    <>
      <Navbar />
      <section className="intro-section" id="home-section">
        <div className="slide-1" data-stellar-background-ratio="0.5">
          <div className="container">
            <div className="row align-items-center">
              <div className="col-12">
                <div className="row align-items-center">
                  <div className="col-lg-6 mb-4">
                    <h1 >
                      Learn From The Expert
                    </h1>
                    <p className="mb-4" >
                      Lorem ipsum dolor sit amet consectetur adipisicing elit.
                      Maxime ipsa nulla sed quis rerum amet natus quas
                      necessitatibus.
                    </p>
                    <p data-aos="fade-up" data-aos-delay="300">
                      <a
                        href="/"
                        className="btn btn-primary py-3 px-5 btn-pill"
                      >
                        Admission Now
                      </a>
                    </p>
                  </div>
                  {!userString && (
                  <div
                    className="col-lg-5 ml-auto"
                    data-aos="fade-up"
                    data-aos-delay="500"
                  >
                    <Signe />
                  </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    <section id="Categories-section">
      <div>
        <div className="site-section courses-title" id="courses-section">
          <div className="container">
            <div className="row mb-5 justify-content-center">
              <div className="col-lg-7 text-center">
                <h2 className="section-title">Categories</h2>
              </div>
            </div>
          </div>
        </div>

        <div className="site-section courses-entry-wrap">
          <div className="container">
              <div style={{ padding: '0 30px' }}>
                <Slider {...settings}>
                  {categoriesWithImages.map((category) => (
                    <div className="List w-96 h-96 py-5 justify-center items-center gap-10 inline-flex" style={{ paddingRight: '15px' }} key={category.id}>
                  <div className="course bg-white h-100 valign-self-stretch">
              <figure className="m-0">
                <img src={category.image} alt="Image" className="img-fluid"/>
              </figure>
              <div className="course-inner-text py-4 px-4">
                <div className="meta"><span className="icon-clock-o"></span></div>
                <h3><a href={`/categories/${category.Nomcategorie}`}>{category.Nomcategorie }</a></h3>
                <p>{category.descreption} </p>
              </div>
            </div>
                </div>
                ))}
              </Slider>
              </div>
            </div>
          </div>
        </div>
      </section>
<section id="Tutorial-section">
      <div className="container">
			<div className="section-title mb-0">
				<h2>Tutorial</h2>
				<p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec malesuada lorem maximus mauris scelerisque, at rutrum nulla dictum. Ut ac ligula sapien. Suspendisse cursus faucibus finibus.</p>
			</div>
		</div>
		<div className="course-warp">
    <ul className="course-filter controls">
  <li
    key="all"
    className={`control ${selectedCategory === 0 ? 'active' : ''}`}
    onClick={() => setSelectedCategory(0)}
  >
    All
  </li>
  {Categories.map((category) => (
    <li
      key={category.id}
      className={`control ${selectedCategory === category.id ? 'active' : ''}`}
      onClick={() => setSelectedCategory(category.id)}
    >
      {category.Nomcategorie}
    </li>
  ))}
</ul>                                     
			<div className="row course-items-area">
      {filteredTutorials.map((tutorial) => (
				<div className="mix col-lg-3 col-md-4 col-sm-6 finance" key={tutorial.tutorial.id}>
					<div className="course-item">
          <a
                href={`/categories/Tutorial/${tutorial.tutorial.NomTutorial}`}
                className="flex-1 rounded-md box-border h-[472px] overflow-hidden flex flex-col items-center justify-start border-[1px] border-solid border-gray-500"
              >
                <div className="self-stretch h-[340px] overflow-hidden shrink-0 flex flex-row items-start justify-start">
                  <div className="self-stretch flex-1 relative bg-gainsboro">
                    <div className="absolute w-[calc(100%_-_32px)] top-[calc(50%_-_8px)] left-[16px] leading-[16px] flex items-center justify-center h-4">
                      Image du tutoriel 1
                    </div>
                  </div>
                </div>
                <div className="self-stretch flex flex-col items-start justify-start p-3 gap-[4px] text-left text-base">
                  <div className="self-stretch relative leading-[24px]">
                    {tutorial.tutorial.NomTutorial}
                  </div>

                  <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-center">
                    <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                      😃
                    </div>
                    <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                      ✅
                    </div>
                    <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                      ❤️
                    </div>
                  </div>
                </div>
              </a>
					</div>
				</div>
        ))}
			</div>
		</div>
	</section>
    <section>
      <div className="self-stretch overflow-hidden flex flex-row items-center justify-center py-[60px] px-[170px] relative gap-[60px] z-[7] text-center">
        <div className="flex-1 flex flex-col items-center justify-start gap-[24px] z-[0]">
          <b className="relative leading-[48px] inline-block w-[520px]">
            Dernières nouvelles
          </b>
          <div className="self-stretch flex flex-row items-center justify-center py-5 px-0 gap-[40px] text-xs">
            <a
              href="/"
              className="flex-1 rounded-md overflow-hidden flex flex-col items-center justify-start border-[1px] border-solid border-gray-500"
            >
              <div className="self-stretch h-[245px] overflow-hidden shrink-0 flex flex-row items-start justify-start">
                <div className="self-stretch flex-1 relative bg-gainsboro">
                  <div className="absolute w-[calc(100%_-_32px)] top-[calc(50%_-_8px)] left-[16px] leading-[16px] flex items-center justify-center h-4">
                    Image de la dernière nouvelle
                  </div>
                  <div className="absolute bottom-[8px] left-[calc(50%_-_22px)] flex flex-row items-center justify-center gap-[4px]">
                    <div className="relative rounded-81xl bg-white w-5 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                  </div>
                </div>
              </div>
              <div className="self-stretch flex flex-col items-start justify-start p-3 gap-[8px] text-left text-base">
                <div className="self-stretch relative leading-[24px] [display:-webkit-inline-box] overflow-hidden text-ellipsis [-webkit-line-clamp:2] [-webkit-box-orient:vertical]">
                  Venez découvrir notre nouveau tutoriel sur les réseaux de
                  neurones !
                </div>
                <div className="self-stretch overflow-hidden flex flex-row items-center justify-start text-xs">
                  <div className="rounded-sm bg-gainsboro overflow-hidden flex flex-row items-center justify-center py-0.5 px-1 border-[0.5px] border-solid border-gray-500">
                    <div className="relative leading-[16px]">Tutoriel</div>
                  </div>
                </div>
                <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-sm">
                  <div className="relative rounded-xl bg-gray-500 w-5 h-5 overflow-hidden shrink-0" />
                  <div className="flex-1 flex flex-col items-start justify-start">
                    <div className="self-stretch relative leading-[20px] font-medium inline-block overflow-hidden text-ellipsis whitespace-nowrap h-5 shrink-0">
                      Jane
                    </div>
                  </div>
                </div>
                <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-center">
                  <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                    👍
                  </div>
                  <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                    💬
                  </div>
                </div>
              </div>
            </a>
            <a
              href="/"
              className="flex-1 rounded-md overflow-hidden flex flex-col items-center justify-start border-[1px] border-solid border-gray-500"
            >
              <div className="self-stretch h-[245px] overflow-hidden shrink-0 flex flex-row items-start justify-start">
                <div className="self-stretch flex-1 relative bg-gainsboro">
                  <div className="absolute w-[calc(100%_-_32px)] top-[calc(50%_-_8px)] left-[16px] leading-[16px] flex items-center justify-center h-4">
                    Image de la dernière nouvelle
                  </div>
                  <div className="absolute bottom-[8px] left-[calc(50%_-_22px)] flex flex-row items-center justify-center gap-[4px]">
                    <div className="relative rounded-81xl bg-white w-5 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                  </div>
                </div>
              </div>
              <div className="self-stretch flex flex-col items-start justify-start p-3 gap-[8px] text-left text-base">
                <div className="self-stretch relative leading-[24px]">
                  Un projet machine learning inspirant à essayer !
                </div>
                <div className="self-stretch overflow-hidden flex flex-row items-center justify-start text-xs">
                  <div className="rounded-sm bg-gainsboro overflow-hidden flex flex-row items-center justify-center py-0.5 px-1 border-[0.5px] border-solid border-gray-500">
                    <div className="relative leading-[16px]">Projet</div>
                  </div>
                </div>
                <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-sm">
                  <div className="relative rounded-xl bg-gray-500 w-5 h-5 overflow-hidden shrink-0" />
                  <div className="flex-1 flex flex-col items-start justify-start">
                    <div className="self-stretch relative leading-[20px] font-medium inline-block overflow-hidden text-ellipsis whitespace-nowrap h-5 shrink-0">
                      Alice
                    </div>
                  </div>
                </div>
                <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-center">
                  <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                    👍
                  </div>
                  <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                    💬
                  </div>
                </div>
              </div>
            </a>
            <a
              href="/"
              className="flex-1 rounded-md overflow-hidden flex flex-col items-center justify-start border-[1px] border-solid border-gray-500"
            >
              <div className="self-stretch h-[245px] overflow-hidden shrink-0 flex flex-row items-start justify-start">
                <div className="self-stretch flex-1 relative bg-gainsboro">
                  <div className="absolute w-[calc(100%_-_32px)] top-[calc(50%_-_8px)] left-[16px] leading-[16px] flex items-center justify-center h-4">
                    Image de la dernière nouvelle
                  </div>
                  <div className="absolute bottom-[8px] left-[calc(50%_-_22px)] flex flex-row items-center justify-center gap-[4px]">
                    <div className="relative rounded-81xl bg-white w-5 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                  </div>
                </div>
              </div>
              <div className="self-stretch flex flex-col items-start justify-start p-3 gap-[8px] text-left text-base">
                <div className="self-stretch relative leading-[24px] [display:-webkit-inline-box] overflow-hidden text-ellipsis [-webkit-line-clamp:2] [-webkit-box-orient:vertical]">
                  Comment optimiser les performances des modèles d'apprentissage
                  machine.
                </div>
                <div className="self-stretch overflow-hidden flex flex-row items-center justify-start text-xs">
                  <div className="rounded-sm bg-gainsboro overflow-hidden flex flex-row items-center justify-center py-0.5 px-1 border-[0.5px] border-solid border-gray-500">
                    <div className="relative leading-[16px]">Astuce</div>
                  </div>
                </div>
                <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-sm">
                  <div className="relative rounded-xl bg-gray-500 w-5 h-5 overflow-hidden shrink-0" />
                  <div className="flex-1 flex flex-col items-start justify-start">
                    <div className="self-stretch relative leading-[20px] font-medium inline-block overflow-hidden text-ellipsis whitespace-nowrap h-5 shrink-0">
                      Bob
                    </div>
                  </div>
                </div>
                <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-center">
                  <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                    👍
                  </div>
                  <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                    💬
                  </div>
                </div>
              </div>
            </a>
            <a
              href="/"
              className="flex-1 rounded-md overflow-hidden flex flex-col items-center justify-start border-[1px] border-solid border-gray-500"
            >
              <div className="self-stretch h-[245px] overflow-hidden shrink-0 flex flex-row items-start justify-start">
                <div className="self-stretch flex-1 relative bg-gainsboro">
                  <div className="absolute w-[calc(100%_-_32px)] top-[calc(50%_-_8px)] left-[16px] leading-[16px] flex items-center justify-center h-4">
                    Image de la dernière nouvelle
                  </div>
                  <div className="absolute bottom-[8px] left-[calc(50%_-_22px)] flex flex-row items-center justify-center gap-[4px]">
                    <div className="relative rounded-81xl bg-white w-5 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                    <div className="relative rounded-81xl bg-gray-300 w-1 h-1" />
                  </div>
                </div>
              </div>
              <div className="self-stretch flex flex-col items-start justify-start p-3 gap-[8px] text-left text-base">
                <div className="self-stretch relative leading-[24px] [display:-webkit-inline-box] overflow-hidden text-ellipsis [-webkit-line-clamp:2] [-webkit-box-orient:vertical]">
                  Les applications de l'apprentissage machine dans le secteur de
                  la santé.
                </div>
                <div className="self-stretch overflow-hidden flex flex-row items-center justify-start text-xs">
                  <div className="rounded-sm bg-gainsboro overflow-hidden flex flex-row items-center justify-center py-0.5 px-1 border-[0.5px] border-solid border-gray-500">
                    <div className="relative leading-[16px]">Actualité</div>
                  </div>
                </div>
                <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-sm">
                  <div className="relative rounded-xl bg-gray-500 w-5 h-5 overflow-hidden shrink-0" />
                  <div className="flex-1 flex flex-col items-start justify-start">
                    <div className="self-stretch relative leading-[20px] font-medium inline-block overflow-hidden text-ellipsis whitespace-nowrap h-5 shrink-0">
                      Jane
                    </div>
                  </div>
                </div>
                <div className="self-stretch flex flex-row items-center justify-start gap-[8px] text-center">
                  <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                    👍
                  </div>
                  <div className="relative leading-[24px] flex items-center justify-center overflow-hidden text-ellipsis whitespace-nowrap w-6 h-6 shrink-0">
                    💬
                  </div>
                </div>
              </div>
            </a>
          </div>
        </div>
      </div>
  </section>
  <section id="contact-section">
      {(user===null||user.type==="user")&&(
      <div className="self-stretch overflow-hidden flex flex-row items-center justify-center py-[60px] px-[170px] relative gap-[60px] z-[8] text-sm">
        <div className="flex-1 flex flex-col items-center justify-start gap-[24px] z-[0]">
          <b className="relative text-21xl leading-[48px] inline-block text-center w-[520px]">
            Contactez-nous
          </b>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Nom
            </div>
            <input
              type="text"
              placeholder="votre nom"
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
            />
          </div>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Email
            </div>
            <input
              type="text"
              placeholder="Nous ne partagerons jamais votre email."
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
            />
          </div>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Message
            </div>
            <input
              type="text"
              placeholder="message."
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
            />
          </div>
          <div className="overflow-hidden flex flex-row items-start justify-start gap-[12px] text-base">
            <a
              href="/"
              className="rounded-lg box-border w-40 flex flex-col items-center justify-center p-3 border-[1px] border-solid border-black"
            >
              <div className="relative leading-[24px] font-medium">Effacer</div>
            </a>
            <a
              href="/"
              className="rounded-lg bg-black w-40 flex flex-col items-center justify-center p-3 box-border text-white"
            >
              <div className="relative leading-[24px] font-medium">Envoyer</div>
            </a>
          </div>
        </div>
      </div>
      )}
    </section>
  <Footer />
</>
);
};

export default HomePage;
