import React, { useState, useEffect } from "react";
import Navbar from "../component/navbar";
import Footer from "../component/footer";
import { useNavigate ,Link } from "react-router-dom";
import '../style/style.css';
import VideoItem from "../component/VideoItem";
const CreatAccount = ({ formData, handleInputChange, handleSubmit }) => (
  <div className="Section w-[1283px] h-[225px] px-[170px] py-[60px] center items-center gap-14 inline-flex">
    <div className="Container grow shrink basis-0 flex-col justify-start items-center gap-6 inline-flex">
      <div className="Title w-96 text-center text-black text-4xl font-bold font-['Roboto'] leading-10">
        Create Account
      </div>
      <div className="Description w-96 text-center text-black text-base font-normal font-['Roboto'] leading-normal">
        Please fill in the required information
      </div>
      <div className="Input h-9 flex-col justify-center items-start gap-1 flex">
        <div className="Textfield self-stretch px-3 py-2 bg-white rounded-md border border-black border-opacity-10 justify-start items-center gap-1 inline-flex">
          <input
            type="text"
            placeholder="Username"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            className="Text grow shrink basis-0 h-5 text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight"
          />
        </div>
      </div>
      <div className="Input h-9 flex-col justify-center items-start gap-1 flex">
        <div className="Textfield self-stretch px-3 py-2 bg-white rounded-md border border-black border-opacity-10 justify-start items-center gap-1 inline-flex">
          <input
            type="text"
            placeholder="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            className="Text grow shrink basis-0 h-5 text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight"
          />
        </div>
      </div>
      <div className="Input h-9 flex-col justify-center items-start gap-1 flex">
        <div className="Textfield self-stretch px-3 py-2 bg-white rounded-md border border-black border-opacity-10 justify-start items-center gap-1 inline-flex">
          <input
            type="radio"
            name="type"
            value="user"
            onChange={handleInputChange}
            className="Text grow shrink basis-0 h-5 text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight"
            checked={formData.type === "user"}
          />
          <label>user</label>
          <input
            type="radio"
            name="type"
            value="admin"
            onChange={handleInputChange}
            className="Text grow shrink basis-0 h-5 text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight"
            checked={formData.type === "admin"}
          />
          <label>admin</label>
        </div>
      </div>
      <div className="Input h-9 flex-col justify-center items-start gap-1 flex">
        <div className="Textfield self-stretch px-3 py-2 bg-white rounded-md border border-black border-opacity-10 justify-start items-center gap-1 inline-flex">
          <input
            type="password"
            placeholder="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            className="Text grow shrink basis-0 h-5 text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight"
          />
        </div>
        <div className="Textfield self-stretch px-3 py-2 bg-white rounded-md border border-black border-opacity-10 justify-start items-center gap-1 inline-flex">
          <input
            type="password"
            placeholder="Confirm password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleInputChange}
            className="Text grow shrink basis-0 h-5 text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight"
          />
        </div>
      </div>
      <div className="Button justify-start items-start gap-3 inline-flex">
        <div className="Primary w-40 p-3 bg-black rounded-lg flex-col justify-center items-center inline-flex">
          <input
            type="button"
            value="Create Account"
            className="Button"
            onClick={handleSubmit}
          />
        </div>
      </div>
    </div>
  </div>
);
function ProfilPage() {
  const userString = sessionStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;
  const navigate = useNavigate();
const [errorMessage, setErrorMessage] = useState("");
const [History, setHistory] = useState([]);
const [historyVideos, setHistoryVideos] = useState([]);
const [messages, setmessages] = useState([]);


const getHistoryVideos = async (History) => {
  try {
    const response = await fetch("http://localhost:3001/historique", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ history: History }), 
    });

    const data = await response.json();

    console.log("Server Response:", data);

    setHistoryVideos(data.videos);
  } catch (error) {
    console.error("Erreur lors de la requête pour récupérer les vidéos d'historique :", error);
  }
};
useEffect(() => {
  if (!user) {
    navigate("/");
  }
}, [user, navigate]);

 const [isCreatAccountVisible, setIsCreatAccountVisible] = useState(false);
const [formData, setFormData] = useState({
  username: "",
  email: "",
  type: "user",
  password: "",
  confirmPassword: "",
});

  const handleSubmit = async () => {
    if (formData.password !== formData.confirmPassword) {
      setErrorMessage("Password and Confirm Password do not match");
      return;
    }
    console.log("Données du formulaire :", formData);
    fetch("http://localhost:3001/user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ formData }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === true) {
          setErrorMessage("creation compte secesseful .");
          setIsCreatAccountVisible(false);
          setFormData({
            username: "",
            email: "",
            type: "user",
            password: "",
            confirmPassword: "",
          });
        } else {
          setErrorMessage(" error.");
        }
      })
      .catch((error) => {
        console.error("Erreur lors de la requête :", error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

 const handleButtonAccount = () => {
  setErrorMessage("");
   setIsCreatAccountVisible(!isCreatAccountVisible);
 };
   const handleLogout = () => {
     sessionStorage.removeItem("user");
     navigate("/");
   };

   useEffect(() => {
    const fetchHistory = async () => {
      fetch("http://localhost:3001/historique", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ user })
    })
      .then((response) => response.json())
      .then((data) => {
        setHistory(data.historique);

        getHistoryVideos(data.historique);
      })
      .catch((error) => {
        console.error("Erreur lors de la requête :", error);
      });
    };
    const fetchContact = async () => {
      try {
        const response = await fetch("http://localhost:3001/contact");
        const data = await response.json();
        setmessages(data.message);
        console.log(data);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    fetchHistory();
    fetchContact();
  },[])

  return (
    <div>
      {user && (
        <>
          <Navbar />
          <section>
      <div className="container bootstrap snippets bootdeys">
        <div className="row">
          <div className="col-md-12">
            <div
              className="bg-picture"
              style={{ backgroundImage: 'url("https://www.bootdey.com/image/700x300/")' }}
            >
              <span className="bg-picture-overlay"></span>
              <div className="box-layout meta bottom">
                <div className="col-md-6 clearfix">
                  <span className="img-wrapper pull-left m-r-15">
                    <img
                      src="https://bootdey.com/img/Content/avatar/avatar1.png"
                      alt=""
                      style={{ width: '64px' }}
                      className="br-radius"
                    />
                  </span>
                  <div className="media-body">
                    <h3 className="text-white mb-2 m-t-10 ellipsis">{user.NomEtPrenom}</h3>
                    <h5 className="text-white"> {user.type}</h5>
                  </div>
                </div>
                </div>
            </div>
          </div>
          <div className="col-md-6 flex-col justify-end items-end gap-3 inline-flex ml-auto">
          <div className="pull-right">
    <div className="flex-col">
      <div className="h-12 p-3 rounded-lg border border-black flex-col justify-center items-center flex">
        <input
          type="button"
          value="Se déconnecter"
          onClick={handleLogout}
          className="text-black text-basefont-['Roboto'] leading-normal"
        />
      </div>
      {user.type === "admin" && (
        <div className="h-12 p-3 Button rounded-lg flex-col justify-center items-center flex">
          <input
            type="button"
            value="Create Account"
            className="Button"
            onClick={handleButtonAccount}
          />
        </div>
      )}
    </div>
  </div>
</div>
        </div>
      </div>
      {isCreatAccountVisible && (
        <div>
          <CreatAccount
            formData={formData}
            handleInputChange={handleInputChange}
            handleSubmit={handleSubmit}
          />
        </div>
      )}
      <span>{errorMessage}</span>
    </section>
        {(user.type==="user") ? (
      <section>
          <div className="w-[1440px] h-[690px] pl-[266px] pr-[301px] py-[60px] justify-center items-center gap-[60px] inline-flex">

            <div className="grow shrink basis-0 flex-col justify-start items-start gap-6 inline-flex">
              <div className="self-stretch text-black text-[40px] font-bold font-['Roboto'] leading-[48px]">
                Video History
              </div>
              <div className="self-stretch text-black text-base font-normal font-['Roboto'] leading-normal">
                Here are the videos you have watched
              </div>
            </div>
              <div className="overflow-y-auto h-[690px]" data-spy="scroll" data-offset="0">
                <div className="w-[630px] h-full py-5 flex-col justify-center items-center gap-10 inline-flex">
                  {historyVideos.map((video) => (
                      <Link key={video.id} to={`/categories/Tutorial`}>
                        <VideoItem video={video} />
                      </Link>
                    ))}
                </div>
              </div>
          </div>
      </section>
      ):(
        <table className="message-table">
      <thead>
        <tr>
          <th>Nom</th>
          <th>Email</th>
          <th>Message</th>
        </tr>
      </thead>
      <tbody>
        {messages.map((message, index) => (
          <tr key={index}>
            <td>{message.nom}</td>
            <td>{message.email}</td>
            <td>{message.message}</td>
          </tr>
        ))}
      </tbody>
    </table>
      )
      }
    <Footer />
        </>
      )}
    </div>
  );
}

export default ProfilPage;
