import React, { useEffect, useState } from "react";
import { useSpring, animated } from 'react-spring';
import { Link, scroller } from 'react-scroll';

import '../style/owl.carousel.css';
import '../style/owl.theme.default.min.css';
import '../style/bootstrap.min.css';

const Navbar = () => {
  const user = sessionStorage.getItem("user");
  const userString = user ? JSON.parse(user) : null;
  const [scrollY, setScrollY] = useState(0);
  const [activeMenuItem, setActiveMenuItem] = useState('Accueil');

  const handleMenuClick = (menuItem) => {
    setActiveMenuItem(menuItem);
    scroller.scrollTo(menuItem, {
      duration: 800,
      delay: 0,
      smooth: 'easeInOutQuart',
    });
  };

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const navbarStyles = useSpring({
    opacity: scrollY > 50 ? 0.8 : 1,
    background: scrollY > 100 ? "#000000" : "#15D4EE",
  });

  return (
    <animated.header style={{ ...navbarStyles, position: 'fixed', width: '100%', zIndex: 1000 }} className="site-navbar py-4 js-sticky-header site-navbar-target" role="banner">
      <div className="container-fluid">
        <div className="d-flex align-items-center">
          <div className="site-logo mr-auto w-25"><a href="/">Makseb Solutions</a></div>
          <div className="mx-auto text-center">
            <nav className="site-navigation position-relative text-right" role="navigation">
              <ul className="site-menu main-menu js-clone-nav mx-auto d-lg-flex m-0 p-0">
                <li><Link to="home-section" className={`nav-link ${activeMenuItem === 'Accueil' ? 'Active' : ''}`} onClick={() => handleMenuClick('Accueil')}>Accueil</Link></li>
                <li><Link to="Categories-section" className={`nav-link ${activeMenuItem === 'Catégories' ? 'Active' : ''}`} onClick={() => handleMenuClick('Catégories')}>Catégories</Link></li>
                <li><Link to="Tutorial-section" className={`nav-link ${activeMenuItem === 'Tutorial' ? 'Active' : ''}`} onClick={() => handleMenuClick('Tutorial')}>Tutorial</Link></li>
                {userString && (
                  <li><a href="/Profil" className="nav-link">Profil</a></li>
                )}
              </ul>
            </nav>
          </div>
          {(user === null || user.type === "user") && (
            <div className="ml-auto w-25">
              <nav className="site-navigation position-relative text-right" role="navigation">
                <ul className="site-menu main-menu site-menu-dark js-clone-nav d-lg-flex m-0 p-0">
                  <li className="cta"><a href="#contact-section" className="nav-link"><span>Contact Us</span></a></li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>
    </animated.header>
  );
};

export default Navbar;
