import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../component/navbar";
import Footer from "../component/footer";
import { Link, useNavigate } from "react-router-dom";

const VideoItem = ({ video }) => (
  <div
    key={video.id}
    className="grow shrink basis-0 h-48 p-4 rounded-md border border-black border-opacity-10 justify-center items-start gap-4 flex"
  >
    <div className="w-[100px] h-[100px] justify-start items-start flex">
      <div className="w-[100px] h-[100px] relative bg-zinc-300 bg-opacity-50"></div>
    </div>
    <div className="grow shrink basis-0 flex-col justify-start items-start gap-2 inline-flex">
      <div className="self-stretch text-black text-xl font-medium font-['Roboto'] leading-7">
        {video.nomvideo}
      </div>
      <div className="self-stretch text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight">
        Step by step guide
      </div>
      <div className="self-stretch text-black text-base font-normal font-['Roboto'] leading-normal">
        {video.description}
      </div>
      <div className="self-stretch py-1 justify-start items-center gap-1.5 inline-flex">
        {/* ... (reste du code pour les boutons et informations) */}
      </div>
    </div>
    <br />
  </div>
); 

const TutorialPage = () => {
    const userString = sessionStorage.getItem("user");
    const user = userString ? JSON.parse(userString) : null;
  const { NomTutorial, nomvideo } = useParams();
  const [Tutorial, setTutorial] = useState([]);
  const [videos, setVideos] = useState([]);
    const navigate = useNavigate();

      useEffect(() => {
        if (!user) {
          navigate("/");
        }
      }, [user, navigate]);
  useEffect(() => {
    const fetchTutorial = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/Tutorial/${NomTutorial}`
        );
        const data = await response.json();
        setTutorial(data.tutorial);
        setVideos(data.tutorial.videos);
        //.log(data.tutorial);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };
    fetchTutorial();
  }, [NomTutorial]);

  return (
    <div>
      <Navbar />
      <div className="w-[1440px]pt-20 bg-white flex-col justify-start items-center inline-flex">
        <div className="w-[1444px] p-[60px] bg-black bg-opacity-60 justify-center items-center gap-[60px] inline-flex">
          <div className="w-[520px] flex-col justify-start items-start gap-6 inline-flex">
            <div className="self-stretch text-white text-[40px] font-bold font-['Roboto'] leading-[48px]">
              {Tutorial.NomTutorial}
            </div>
            <div className="self-stretch text-white text-base font-normal font-['Roboto'] leading-normal">
              {Tutorial.description}
            </div>
          </div>
          <div className="w-[520px] h-[400px] py-1 justify-start items-start flex"></div>
        </div>
      </div>
      <div className="w-[630px] h-[880px] py-5 flex-col justify-center items-center gap-10 inline-flex">
        {videos.map((itemV) => (
          <Link key={itemV.id} to={`/categories/Tutorial/${Tutorial.NomTutorial}/${itemV.nomvideo}`}>
            <VideoItem  video={itemV} />
          </Link>
        ))}
      </div>
      <Footer />
    </div>
  );
};

export default TutorialPage;
