import React, { useEffect, useState } from "react";
import { useSpring, animated } from 'react-spring';
import { useInView } from 'react-intersection-observer';

import '../style/owl.carousel.css';
import '../style/owl.theme.default.min.css';
import '../style/bootstrap.min.css';

const Navbar = () => {
  const [activeMenuItem, setActiveMenuItem] = useState('Accueil');
  const [refAccueil, inViewAccueil] = useInView({ triggerOnce: true });
  const [refCategories, inViewCategories] = useInView({ triggerOnce: true });
  const [refTutorial, inViewTutorial] = useInView({ triggerOnce: true });

  const handleMenuClick = (menuItem) => {
    setActiveMenuItem(menuItem);
  };

  useEffect(() => {
    if (inViewAccueil) {
      setActiveMenuItem('Accueil');
    } else if (inViewCategories) {
      setActiveMenuItem('Catégories');
    } else if (inViewTutorial) {
      setActiveMenuItem('Tutorial');
    }
  }, [inViewAccueil, inViewCategories, inViewTutorial]);

  const navbarStyles = useSpring({
    opacity: scrollY > 50 ? 0.8 : 1,
    background: scrollY > 100 ? "#000000" : "#7971ea",
  });

  return (
    <animated.header style={{ ...navbarStyles, position: 'fixed', width: '100%', zIndex: 1000 }} className="site-navbar py-4 js-sticky-header site-navbar-target" role="banner">
      <div className="container-fluid">
        <div className="d-flex align-items-center">
          <div className="site-logo mr-auto w-25"><a href="/">TutorialMachine</a></div>
          <div className="mx-auto text-center">
            <nav className="site-navigation position-relative text-right" role="navigation">
              <ul className="site-menu main-menu js-clone-nav mx-auto d-none d-lg-block  m-0 p-0">
                <li><a ref={refAccueil} href="/#home-section" className={`nav-link ${activeMenuItem === 'Accueil' ? 'active' : ''}`} onClick={() => handleMenuClick('Accueil')}>Accueil</a></li>
                <li><a ref={refCategories} href="/#Categories-section" className={`nav-link ${activeMenuItem === 'Catégories' ? 'active' : ''}`} onClick={() => handleMenuClick('Catégories')}>Catégories</a></li>
                <li><a ref={refTutorial} href="/#Tutorial-section" className={`nav-link ${activeMenuItem === 'Tutorial' ? 'active' : ''}`} onClick={() => handleMenuClick('Tutorial')}>Tutorial</a></li>
                {/* Ajoutez d'autres liens de menu ici */}
              </ul>
            </nav>
          </div>
          <div className="ml-auto w-25">
            <nav className="site-navigation position-relative text-right" role="navigation">
              <ul className="site-menu main-menu site-menu-dark js-clone-nav mr-auto d-none d-lg-block m-0 p-0">
                <li className="cta"><a href="#contact-section" className="nav-link"><span>Contact Us</span></a></li>
              </ul>
            </nav>
          </div>
        </div>
      </div>
    </animated.header>
  );
};

export default Navbar;
