import React, { useEffect, useState } from "react";
import { useSpring, animated } from 'react-spring';

import '../style/owl.carousel.css';
import '../style/owl.theme.default.min.css';
import '../style/bootstrap.min.css';

const Navbar = () => {
  const user = sessionStorage.getItem("user");
  const userString = user ? JSON.parse(user) : null;
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Utilisation de react-spring pour gérer l'animation de défilement
  const navbarStyles = useSpring({
    opacity: scrollY > 100 ? 0.8 : 1,
    background: scrollY > 100 ? "#000000" : "transparent",
  });

  return (
    <div>
    <animated.header style={{ ...navbarStyles, position: 'fixed', width: '100%', zIndex: 1000 }} className="site-navbar py-4 js-sticky-header site-navbar-target" role="banner">
      <div className="container-fluid">
        <div className="d-flex align-items-center">
          <div className="site-logo mr-auto w-25"><a href="/">TutorialMachine</a></div>
          <div className="mx-auto text-center">
            <nav className="site-navigation position-relative text-right" role="navigation">
              <ul className="site-menu main-menu js-clone-nav mx-auto d-none d-lg-block  m-0 p-0">
                <li><a href="/#home-section" className="nav-link"  >Accueil</a></li>
                <li><a href="/#Categories-section" className="nav-link" >Catégories</a></li>
                <li><a href="/#Tutorial-section" className="nav-link">Tutorial</a></li>
                {userString && (
                  <li><a href="/Profil" className="nav-link">Profil</a></li>
                )}
              </ul>
            </nav>
          </div>
          {(user===null||user.type==="user")&&(
          <div className="ml-auto w-25">
            <nav className="site-navigation position-relative text-right" role="navigation">
              <ul className="site-menu main-menu site-menu-dark js-clone-nav mr-auto d-none d-lg-block m-0 p-0">
                <li className="cta"><a href="#contact-section" className="nav-link"><span>Contact Us</span></a></li>
              </ul>
            </nav>
            <a href="/#" className="d-inline-block d-lg-none site-menu-toggle js-menu-toggle text-black float-right"><span className="icon-menu h3"></span></a>
          </div>
          )}
        </div>
      </div>
    </animated.header>
    </div>
  );
};

export default Navbar;
