import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../component/navbar";
import Footer from "../component/footer";
import { Link, useNavigate } from "react-router-dom";
import VideoItem from "../component/VideoItem";

function VideosPage() {
  const userString = sessionStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;
  const { NomTutorial, Nomvideo } = useParams();
  const [Tutorial, setTutorial] = useState([]);
  const [video, setVideo] = useState([]);
  const [datavideo, setdataVideo] = useState([]);
  const [videos, setVideos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      sessionStorage.setItem("error", JSON.stringify("verifie son compte"));
      navigate("/");
    }
  }, [user, navigate]);

  useEffect(() => {
    const Enrehistory = async () => {
      const vue = {
        id_user: user.id,
        id_video: video.id,
      };
  
      try {
        const response = await fetch("http://localhost:3001/historique", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ vue }),
        });
  
        const data = await response.json();
        console.log(data);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };
  
 
    if (video.id) {
      Enrehistory();
    }
  }, ); 
  useEffect(() => {
    const fetchTutorial = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/Tutorial/${NomTutorial}`
        );
        const data = await response.json();
        setTutorial(data.tutorial);
        setVideos(data.tutorial.videos);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    const fetchVideo = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/Tutorial/${NomTutorial}/${Nomvideo}`
        );
        const data = await response.json();
        console.log(data);
        setVideo(data.video);
        setdataVideo(data.video[0].video.data);
        
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    fetchTutorial();
    fetchVideo();
  }, [NomTutorial, Nomvideo]);

  return (
    <div>
      <Navbar />
      <div className="video-page">
        <div className="video-container">
          <video width="800" controls>
            <source
              src={`data:video/mp4;base64,${btoa(String.fromCharCode.apply(null, new Uint8Array(datavideo)))}`}
              type="video/mp4"
            />
          </video>
        </div>
        <div className="video-list">
          {videos.map((itemV) => (
            <Link
              key={itemV.id}
              to={`/categories/Tutorial/${Tutorial.NomTutorial}/${itemV.nomvideo}`}
            >
              <VideoItem video={itemV} />
            </Link>
          ))}
        </div>
        <div className="video-details">
          <h2>{video.nomvideo}</h2>
          <p>{video.description}</p>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default VideosPage;
