/* eslint-disable jsx-a11y/img-redundant-alt */
// HomePage.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { SlArrowLeftCircle, SlArrowRightCircle } from "react-icons/sl";

import Navbar from "../component/navbar";
import Footer from "../component/footer";
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import '../style/style.css';



const Signe = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  // eslint-disable-next-line no-unused-vars
  const [user, setUser] = useState([]);
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    setErrorMessage("");
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setErrorMessage("");
  };

  const handleSignIn = () => {
    if (!email.trim() || !password.trim()) {
      setErrorMessage("Please fill in both email and password.");
      return;
    }

    fetch("http://localhost:3001/user", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        setUser(data.results);
        if (data.message === false) {
          setErrorMessage("Il n'y a pas de compte avec ces informations.");
        } else {
          sessionStorage.setItem("user", JSON.stringify(data.results[0]));
          navigate("/profil");
        }
      })
      .catch((error) => {
        console.error("Erreur lors de la requête :", error);
      });
  };

  return (
    <form method="post" className="form-box text-center">
  <h3 className="h4 text-black mb-4">Connection</h3>
  <div className="form-group">
    <input
      type="text"
      name="email"
      autoComplete="email"
      required
      value={email}
      onChange={handleEmailChange}
      className="form-control"
      placeholder="Email Address"
    />
  </div>
  <div className="form-group">
    <input
      type="password"
      className="form-control"
      name="password"
      autoComplete="current-password"
      required
      value={password}
      onChange={handlePasswordChange}
      placeholder="Password"
    />
  </div>
  <div className="form-group">
    <input
      type="button"
      onClick={handleSignIn}
      className="btn btn-pill btn-light-blue"
      value="Sign up"
    />
  </div>
  {errorMessage && (
    <span style={{ color: "red" }}>{errorMessage}</span>
  )}
</form>

  );
};



const Contact =()=>{
  const [nom, setNom] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const Envoyer = () => {
    fetch("http://localhost:3001/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({  nom , email , message }),
    })
      .then((response) => response.json())
      .then((data) => {
        //console.log(data)
      })
      .catch((error) => {
        console.error("Erreur lors de la requête :", error);
      });
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleNomChange = (e) => {
    setNom(e.target.value);
  };
  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };
  const Effacer = () =>{
    setEmail("");
    setNom("");
    setMessage("");
  }

  return(
    <div className="self-stretch overflow-hidden flex flex-row items-center justify-center py-[60px] px-[170px] relative gap-[60px] z-[8] text-sm">
        <div className="flex-1 flex flex-col items-center justify-start gap-[24px] z-[0]">
          <b className="relative text-21xl leading-[48px] inline-block text-center w-[520px]">
            Contactez-nous
          </b>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Nom
            </div>
            <input
              type="text"
              name="nom"
              onChange={handleNomChange}
              value={nom}
              placeholder="votre nom"
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
            />
          </div>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Email
            </div>
            <input
              type="email"
              name="email"
              onChange={handleEmailChange}
              value={email}
              placeholder="votre email."
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
            />
          </div>
          <div className="w-[600px] overflow-hidden flex flex-col items-start justify-center gap-[4px]">
            <div className="self-stretch relative leading-[20px] font-medium">
              Message
            </div>
            <textarea
              className="self-stretch rounded-md bg-white flex flex-row items-center justify-start py-2 px-3 text-gray-400 border-[1px] border-solid border-gray-500"
              name="message"
              rows="4"
              cols="50"
              value={message}
              onChange={handleMessageChange}
              placeholder="votre message."
              />
          </div>
          <div className="overflow-hidden flex flex-row items-start justify-start gap-[12px] text-base">
            <input
              type="button"
              value="Effacer"
              onClick={Effacer}
              className="rounded-lg box-border w-40 flex flex-col items-center justify-center p-3 border-[1px] border-solid border-black"
            />
            <input
              type="button"
              value="Envoyer"
              onClick={Envoyer}
              className="rounded-lg box-border w-40 flex flex-col items-center justify-center p-3 border-[1px] border-solid border-black"
            />
          </div>
        </div>
      </div>
  )
}

const PopuVideos = () => {
  const [videos, setVideos] = useState([]);
  const [Tutorials, setTutorials] = useState([]);
  const [videosTrie, setVideosTrie] = useState([]);

  useEffect(() => {
    const fetchvideos = async () => {
      try {
        const response = await fetch(`http://localhost:3001/video`);
        const data = await response.json();
        setVideos(data.videos);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    const fetchTutorials = async () => {
      try {
        const response = await fetch("http://localhost:3001/Tutorial");
        const data = await response.json();
        setTutorials(data.tutorialsWithVideos);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    fetchTutorials();
    fetchvideos();
  }, []); // Le tri se fera dans le prochain useEffect dépendant de 'videos'

  useEffect(() => {
    const sortByNbVueDesc = () => {
      const sortedVideos = [...videos].sort((a, b) => b.nbvue - a.nbvue);
      setVideosTrie(sortedVideos);
    };

    sortByNbVueDesc();
  }, [videos]);

  const getTutorialNameById = (tutorialId) => {
    const tutorial = Tutorials.find((t) => t.id === tutorialId);
    return tutorial ? tutorial.NomTutorial : "";
  };

  return (
    <>
      <div className=" page_section">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="section_title text-center">
                <h1>Popular Videos</h1>
              </div>
            </div>
          </div>

          <div className="row course_boxes">
            {videosTrie.slice(0, 3).map((video) => (
              <div className="grow shrink basis-0 h-48 p-4 rounded-md border border-black border-opacity-10 justify-center items-start gap-4 flex mr-4 ml-4" key={video.id}>
              <div className="w-[100px] h-[100px] justify-start items-start flex">
                <div className="w-[100px] h-[100px] relative bg-zinc-300 bg-opacity-50"></div>
              </div>
              <a className="btn-pill" href={`/categories/Tutorial/${getTutorialNameById(video.tutorial)}/${video.nomvideo}`}>
              <div className="grow shrink basis-0 flex-col justify-start items-start gap-2 inline-flex">
                <div className="self-stretch text-black text-xl font-medium font-['Roboto'] leading-7">
                  {video.nomvideo}
                </div>
                <div className="self-stretch text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight">
                  Step by step guide
                </div>
                <div className="self-stretch text-black text-base font-normal font-['Roboto'] leading-normal">
                  {video.description}
                </div>
              </div>
              </a>
              <br />
            </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}






const CustomPrevArrow = (props) => (
  <div {...props} className="slick-prev">
    <SlArrowLeftCircle />
  </div>
);

const CustomNextArrow = (props) => (
  <div {...props} className="slick-next">
    <SlArrowRightCircle/>
  </div>
);








function HomePage() {
  const userString = sessionStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;
  const [Categories, setCategories] = useState([]);
  const [Tutorials, setTutorials] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(0);
  const [filteredTutorials, setFilteredTutorials] = useState(Tutorials);


  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch("http://localhost:3001/categories");
        const data = await response.json();
        setCategories(data.categories);
        //console.log(data.categories)
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    const fetchTutorialsWithVideos = async () => {
      try {
        const response = await fetch("http://localhost:3001/Tutorial");
        const data = await response.json();
        setTutorials(data.tutorialsWithVideos);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };

    fetchTutorialsWithVideos();
    fetchCategories();

  }, []); 

  useEffect(() => {
    if (selectedCategory === 0) {
      setFilteredTutorials(Tutorials);
    } else {
      const filtered = Tutorials.filter(tutorial => tutorial.categorie === selectedCategory);
      setFilteredTutorials(filtered);
    }
  }, [selectedCategory, Tutorials]);


  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
