      {
        breakpoint: 1000,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 5,
          infinite: true,
          dots: true,
          margin: 30,
          prevArrow: <CustomPrevArrow />, 
          nextArrow: <CustomNextArrow />,
        }
      }
