          <div className="container">
              <div style={{ padding: '0 30px' }}>
                <Slider {...settings} currentSlide={1} slideCount={3}>
                  {Categories.map((category) => (
                    <div className="List w-96 h-96 py-5 justify-center items-center gap-10 inline-flex" style={{ paddingRight: '15px' }} key={category.id}>
                      <a href={`categories/${category.Nomcategorie}`}>
                        <div className="course bg-white h-100 mr-4 valign-self-stretch">
                          <figure className="m-0">
                            <img src={category.imagePath} alt={category.imagePath} className="img-fluid ml-4"/>
