import React, { useEffect, useState } from "react";
import { SlArrowLeftCircle, SlArrowRightCircle } from "react-icons/sl";
import Navbar from "../component/navbar";
import Footer from "../component/footer";
import VideoItem from "../component/VideoItem";
import { Link, useNavigate } from "react-router-dom";



function CategoriesPage() {
    const userString = sessionStorage.getItem("user");
    const user = userString ? JSON.parse(userString) : null;
  const [tutorialsWithVideos, settutorialsWithVideos] = useState([]);
  const [Categories, setCategories] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const categoriesPerPage = 3;
    const navigate = useNavigate();

    useEffect(() => {
      if (!user) {
        sessionStorage.setItem("error", JSON.stringify("verifie son compte"));
        navigate("/");
      }
    }, [user, navigate]);
  useEffect(() => {
    const fetchtutorialsWithVideos = async () => {
      try {
        const response = await fetch("http://localhost:3001/Tutorial");
        const data = await response.json();
        settutorialsWithVideos(data.tutorialsWithVideos);

      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };
    const fetchCategories = async () => {
      try {
        const response = await fetch("http://localhost:3001/categories");
        const data = await response.json();
        setCategories(data.categories);
      } catch (error) {
        console.error("Erreur lors de la requête :", error);
      }
    };
    fetchtutorialsWithVideos();
    fetchCategories();
  }, []);

  const getCurrentCategories = () => {
    const startIndex = (currentPage - 1) * categoriesPerPage;
    const endIndex = startIndex + categoriesPerPage;
    return Categories.slice(startIndex, endIndex);
  };

  const nextPage = () => {
    setCurrentPage((prevPage) =>
      Math.min(prevPage + 1, Math.ceil(Categories.length / categoriesPerPage))
    );
  };
  const prevPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };



  return (
    <div>
      <Navbar />
      <div className="w-[1440px] h-[3160px] pt-20 bg-white flex-col justify-start items-center inline-flex">
        <div className="w-[1444px] p-[60px] bg-black bg-opacity-60 justify-center items-center gap-[60px] inline-flex">
          <div className="w-[520px] flex-col justify-start items-start gap-6 inline-flex">
            <div className="self-stretch text-white text-[40px] font-bold font-['Roboto'] leading-[48px]">
              Tutorials
            </div>
            <div className="self-stretch text-white text-base font-normal font-['Roboto'] leading-normal">
              Learn something new!
            </div>
            <div className="h-9 flex-col justify-center items-start gap-1 flex">
              <div className="self-stretch px-3 py-2 bg-white rounded-md border border-black border-opacity-10 justify-start items-center gap-1 inline-flex">
                <input
                  type="text"
                  placeholder="Search for tutorials"
                  className="grow shrink basis-0 h-5 text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight"
                />
                <div className="justify-start items-start gap-3 inline-flex">
                  <div className="w-40 p-3 bg-black rounded-lg flex-col justify-center items-center inline-flex">
                    <input type="button" value="Search" className="Button" />
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="w-[520px] h-[400px] py-1 justify-start items-start flex">
            <div className="w-[520px] h-[392px] relative bg-zinc-300 bg-opacity-50 rounded-md"></div>
          </div>
        </div>

        <div className="self-stretch py-[60px] justify-center items-center inline-flex">
          <div className="grow shrink basis-0 pl-[15px] flex-col justify-start items-center gap-6 inline-flex">
            <div className="w-[520px] text-center text-black text-[40px] font-bold font-['Roboto'] leading-[48px]">
              Categories
            </div>
            <div className="w-[520px] text-center text-black text-base font-normal font-['Roboto'] leading-normal">
              Browse tutorials by category
            </div>
            <div className="categoryContainer">
              {getCurrentCategories().map((category) => (
                <div
                  key={category.id}
                  className="self-stretch py-5 justify-center items-start gap-10 inline-flex"
                >
                  <Link to={`/categories/${category.Nomcategorie}`}>
                    <div className="grow shrink basis-0 py-3 flex-col justify-center items-center gap-5 inline-flex">
                      <div className="w-[100px] h-[100px] bg-black bg-opacity-5 rounded-[50px] justify-center items-center inline-flex">
                        <div className="w-[100px] self-stretch text-center text-black text-[62.50px] font-normal font-['Roboto'] leading-[100px]">
                          📹
                        </div>
                      </div>
                      <div className="self-stretch h-[60px] flex-col justify-start items-start gap-2 flex">
                        <div className="self-stretch text-center text-black text-xl font-normal font-['Roboto'] leading-7">
                          {category.Nomcategorie}
                        </div>
                        <div className="self-stretch text-center text-black text-opacity-50 text-base font-normal font-['Roboto'] leading-normal">
                          {category.descreption}
                        </div>
                      </div>
                      <div className="self-stretch text-center text-black text-[28px] font-medium font-['Roboto'] leading-9">
                        videos
                      </div>
                    </div>
                  </Link>
                </div>
              ))}
              {Categories.length > categoriesPerPage && (
                <div>
                  <button onClick={prevPage}>
                    <SlArrowLeftCircle className="navButtonDroit" />
                  </button>
                  <button onClick={nextPage}>
                    <SlArrowRightCircle className="navButtonGauche" />
                  </button>
                </div>
              )}
            </div>
            <section id="tutorials">
              <div className="w-[1440px] text-center text-black text-[40px] font-bold font-['Roboto'] leading-[48px]">
                tutorials
              </div>
              <div className="w-[1440px] text-center text-black text-base font-normal font-['Roboto'] leading-normal">
                Découvrez nos tutoriels
              </div>
              {tutorialsWithVideos.map(
                (item) =>
                  item.tutorial.videos &&
                  item.tutorial.videos.length > 0 && (
                    <div
                      key={item.tutorial.id}
                      className="self-stretch p-[60px] justify-center items-center gap-[60px] inline-flex"
                    >
                      <div className="grow shrink basis-0 flex-col justify-start items-start gap-6 inline-flex">
                        <div className="self-stretch text-black text-[40px] font-bold font-['Roboto'] leading-[48px]">
                          {item.tutorial.NomTutorial}
                        </div>
                        <div className="self-stretch text-black text-base font-normal font-['Roboto'] leading-normal">
                          {item.tutorial.description}
                        </div>
                      </div>
                      <div className="w-[630px] h-[464px] py-5 flex-col justify-center items-center gap-10 inline-flex">
                        {item.tutorial.videos.slice(0, 2).map((itemV) => (
                          <Link key={itemV.id} to={`/categories/Tutorial/${item.tutorial.NomTutorial}/${itemV.nomvideo}`}>
                              <VideoItem video={itemV} />
                          </Link>
                        ))}
                        {item.tutorial.videos.length > 2 && <a href={`/categories/Tutorial/${item.tutorial.NomTutorial}`}>Voir plus</a>}
                      </div>
                    </div>
                  )
              )}
            </section>
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
}

export default CategoriesPage;
