import React, { useEffect, useState } from "react";
import { useSpring, animated } from "react-spring";
import { BsPersonCircle } from "react-icons/bs";
import { Link as ScrollLink } from "react-scroll";
import { useLocation, useNavigate } from "react-router-dom";

import "../style/owl.carousel.css";
import "../style/owl.theme.default.min.css";
import "../style/bootstrap.min.css";

const Navbar = () => {
  const user = sessionStorage.getItem("user");
  const userString = user ? JSON.parse(user) : null;
  const [scrollY, setScrollY] = useState(0);
  const location = useLocation();
  const [activeMenuItem, setActiveMenuItem] = useState(location.pathname);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const navigate = useNavigate();

  const handleMenuClick = (menuItem) => {
    setActiveMenuItem(menuItem);
    if (menuItem === "/Profil") {
      setActiveMenuItem("");
    }
  };

  const NavigatPrfil = (location) => {
    if (location.pathname === "/Profil") {
      navigate(location);
    } else {
      handleMenuClick(location);
    }
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
    console.log(isMobileMenuOpen)
  };

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const navbarStyles = useSpring({
    opacity: scrollY > 50 ? 0.8 : 1,
    background: scrollY > 100 ? "#000000" : "#15D4EE",
  });

  return (
    <animated.header
      style={{
        ...navbarStyles,
        position: "fixed",
        width: "100%",
        zIndex: 1000,
      }}
      className="site-navbar py-4 js-sticky-header site-navbar-target"
      role="banner"
    >
      <div className="container-fluid">
        <div className="d-flex align-items-center">
          <div className="site-logo mr-auto w-25">
            <a href="/">Makseb Solutions</a>
          </div>
          <div className="mx-auto text-center">
            <nav
              className="site-navigation position-relative text-right"
              role="navigation"
            >
              <ul className="site-menu main-menu js-clone-nav mx-auto d-lg-flex m-0 p-0">
                <li>
                  <ScrollLink
                    onClick={() => NavigatPrfil("/#home-section")}
                    to="home-section"
                    spy={true}
                    smooth={true}
                    offset={-70}
                    duration={500}
                    className={`nav-link ${
                      (activeMenuItem === "/#home-section" ||
                        location.pathname === "/Profil") &&
                      "Nav-Active"
                    }`}
                  >
                    Accueil
                  </ScrollLink>
                </li>
                <li>
                  <ScrollLink
                    onClick={() => NavigatPrfil("/#Categories-section")}
                    to="Categories-section"
                    spy={true}
                    smooth={true}
                    offset={-70}
                    duration={500}
                    className={`nav-link ${
                      (activeMenuItem === "/#Categories-section" ||
                        location.pathname === "/Profil") &&
                      "Nav-Active"
                    }`}
                  >
                    Catégories
                  </ScrollLink>
                </li>
                <li>
                  <ScrollLink
                    onClick={() => NavigatPrfil("/#Tutorial-section")}
                    to="Tutorial-section"
                    spy={true}
                    smooth={true}
                    offset={-70}
                    duration={500}
                    className={`nav-link ${
                      (activeMenuItem === "/#Tutorial-section" ||
                        location.pathname === "/Profil") &&
                      "Nav-Active"
                    }`}
                  >
                    Tutorial
                  </ScrollLink>
                </li>
                <li>
                  <a href="Profil">
                    <BsPersonCircle />
                  </a>
                </li>
              </ul>
            </nav>
            <div className="mobile-menu-icon" onClick={() =>toggleMobileMenu()}>
              ☰
            
              <div
                className={`navbar-links ${
                  isMobileMenuOpen ? "active" : "hidden"
                }`}
              >
                <ul className="site-menu main-menu js-clone-nav d-lg-flex m-0 p-0">
                  <li>
                    <ScrollLink
                      onClick={() => NavigatPrfil("/#home-section")}
                      to="home-section"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}
                      className={`nav-link ${
                        (activeMenuItem === "/#home-section" ||
                          location.pathname === "/Profil") &&
                        "Nav-Active"
                      }`}
                    >
                      Accueil
                    </ScrollLink>
                  </li>
                  <li>
                    <ScrollLink
                      onClick={() => NavigatPrfil("/#Categories-section")}
                      to="Categories-section"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}
                      className={`nav-link ${
                        (activeMenuItem === "/#Categories-section" ||
                          location.pathname === "/Profil") &&
                        "Nav-Active"
                      }`}
                    >
                      Catégories
                    </ScrollLink>
                  </li>
                  <li>
                    <ScrollLink
                      onClick={() => NavigatPrfil("/#Tutorial-section")}
                      to="Tutorial-section"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}
                      className={`nav-link ${
                        (activeMenuItem === "/#Tutorial-section" ||
                          location.pathname === "/Profil") &&
                        "Nav-Active"
                      }`}
                    >
                      Tutorial
                    </ScrollLink>
                  </li>
                </ul>
              </div>
            </div>
          </div>
          {userString.type === "user" && (
            <div className="ml-auto w-25">
              <nav
                className="site-navigation position-relative text-right"
                role="navigation"
              >
                <ul className="site-menu main-menu site-menu-dark js-clone-nav d-lg-flex m-0 p-0">
                  <li className="cta">
                    <a href="#contact-section" className="nav-link">
                      <span>Contact Us</span>
                    </a>
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </div>
      </div>
    </animated.header>
  );
};

export default Navbar;
