// CategoryDetailPage.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../component/navbar";
import Footer from "../component/footer";
import { Link, useNavigate } from "react-router-dom";

const VideoItem = ({ video }) => (
  <div  key={video.id} className="grow shrink basis-0 h-48 p-4 rounded-md border border-black border-opacity-10 justify-center items-start gap-4 flex">
    <div className="w-[100px] h-[100px] justify-start items-start flex">
      <div className="w-[100px] h-[100px] relative bg-zinc-300 bg-opacity-50"></div>
    </div>
    <div className="grow shrink basis-0 flex-col justify-start items-start gap-2 inline-flex">
      <div className="self-stretch text-black text-xl font-medium font-['Roboto'] leading-7">
        {video.nomvideo}
      </div>
      <div className="self-stretch text-black text-opacity-50 text-sm font-normal font-['Roboto'] leading-tight">
        Step by step guide
      </div>
      <div className="self-stretch text-black text-base font-normal font-['Roboto'] leading-normal">
        {video.description}
      </div>
      <div className="self-stretch py-1 justify-start items-center gap-1.5 inline-flex">
        {/* ... (reste du code pour les boutons et informations) */}
      </div>
    </div>
    <br />
  </div>
); 
const CategoryDetailPage = () => {
  const userString = sessionStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;
  const { Nomcategorie } = useParams();
  const [category, setCategory] = useState([]);
  const [tutorialsWithVideos, setTutorialsWithVideos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      sessionStorage.setItem("error", JSON.stringify("verifie son compte"));
      navigate("/");
    }
  }, [user, navigate]);
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch(
          `http://localhost:3001/categories/${Nomcategorie}`
        );
        const data = await response.json();
        setCategory(data.category[0]);
        setTutorialsWithVideos(data.tutorialsWithVideos);
        console.log(data.category[0]);
      } catch (error) {
        console.error("Erreur lors de la requÃªte :", error);
      }
    };
    fetchCategories();
  }, [Nomcategorie]);
  return (
    <div>
      <Navbar />

      <div className="self-stretch py-[60px] justify-center items-center inline-flex">
        <div className="grow shrink basis-0 pl-[15px] flex-col justify-start items-center gap-6 inline-flex">
          <div className="w-[1440px] pt-20 bg-white flex-col justify-start items-center inline-flex">
            <div className="w-[1444px] p-[60px] bg-black bg-opacity-60 justify-center items-center gap-[60px] inline-flex">
              <div className="w-[520px] flex-col justify-start items-start gap-6 inline-flex">
                <div className="self-stretch text-white text-[40px] font-bold font-['Roboto'] leading-[48px]">
                  {category.Nomcategorie}
                </div>
                <div className="self-stretch text-white text-base font-normal font-['Roboto'] leading-normal">
                  {category.descreption}
                </div>
              </div>
              <div className="w-[520px] h-[400px] py-1 justify-start items-start flex"></div>
            </div>
          </div>
          <div>
            {tutorialsWithVideos.map(
              (item) =>
                item.tutorial.videos.length > 0 && (
                  <div
                    key={item.tutorial.NomTutorial}
                    className="self-stretch p-[60px] justify-center items-center gap-[60px] inline-flex"
                  >
                    <div className="grow shrink basis-0 flex-col justify-start items-start gap-6 inline-flex">
                      <div className="self-stretch text-black text-[40px] font-bold font-['Roboto'] leading-[48px]">
                        {item.tutorial.NomTutorial}
                      </div>
                      <div className="self-stretch text-black text-base font-normal font-['Roboto'] leading-normal">
                        {item.tutorial.description}
                      </div>
                    </div>
                    <div className="w-[630px] h-[464px] py-5 flex-col justify-center items-center gap-10 inline-flex">
                      {item.tutorial.videos.slice(0, 2).map((itemV) => (
                        <Link
                          key={itemV.id}
                          to={`/categories/Tutorial/${item.tutorial.NomTutorial}/${itemV.nomvideo}`}
                        >
                          <VideoItem video={itemV} />
                        </Link>
                      ))}
                      {item.tutorial.videos.length > 2 && (
                        <a
                          href={`/categories/Tutorial/${item.tutorial.NomTutorial}`}
                        >
                          Voir plus
                        </a>
                      )}
                    </div>
                  </div>
                )
            )}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default CategoryDetailPage;
